    private void btnAddObservationActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddObservationActionPerformed
        if (lstWatchListName.getSelectedIndex() < 1) {
            Utilities.InfoBox("Please select an animal from the list.", "New Observation");
        }
        else {
            selectedIndex = lstWatchListName.getSelectedIndex()-1;            
            NewObservation newObservation = new NewObservation();
            newObservation.setVisible(true);
        }
    }//GEN-LAST:event_btnAddObservationActionPerformed

