    public void PopulateWatchList() {
        ArrayList<String> listName = new ArrayList<>();
        ArrayList<String> listThreat = new ArrayList<>();
        
        listName.add("Common animal name");
        listThreat.add("Threat Level");
        
        Utilities.SortLists();
        
        for (Animal a : BWA.watchList) {
            listName.add(a.getName());
            listThreat.add(Integer.toString(a.getThreatLevel()));
        }
            lstWatchListName.setListData(listName.toArray());
            lstWatchListThreat.setListData(listThreat.toArray());
    }
    
    public void PopulateObserverList() {
        ArrayList<String> listName = new ArrayList<>();
        ArrayList<String> listAddress = new ArrayList<>();
        ArrayList<String> listPhoneNo = new ArrayList<>();
        ArrayList<String> listEmail = new ArrayList<>();
                
        listName.add("Observer Name");
        listAddress.add("Address");
        listPhoneNo.add("Phone Number");
        listEmail.add("Email Address");
        
        Utilities.SortLists();
        
        for (Observer x : BWA.observers) {
            listName.add(x.getName());
            listAddress.add(x.getAddress());
            listPhoneNo.add(x.getPhoneNo());
            listEmail.add(x.getEmail());
        }
        
        lstObserverName.setListData(listName.toArray());
        lstAddress.setListData(listAddress.toArray());
        lstPhoneNo.setListData(listPhoneNo.toArray());
        lstEmail.setListData(listEmail.toArray());
    }
    
    private void btnRemoveAnimalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemoveAnimalActionPerformed
        if (lstWatchListName.getSelectedIndex() < 1) {
            Utilities.InfoBox("Please select an animal from the list.", "Remove Animal");
        }
        else {
            if (JOptionPane.showConfirmDialog(null, "Are you sure you wish to remove the animal from the watch list?", "Request", 
                    JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE)
                    == JOptionPane.YES_OPTION) {
                int index = lstWatchListName.getSelectedIndex();
                BWA.watchList.remove(index-1);
                PopulateWatchList();
            }
            else {}                
        }
    }//GEN-LAST:event_btnRemoveAnimalActionPerformed

    private int IndexFix(int x) {
        if (x == 0) {
            x = 1;
        }
        return x;
    }
    
    private void AnimalIndex(int index) {
        int newIndex;
        
        newIndex = IndexFix(index);
        SetAnimalIndex(newIndex);
    }
    
    private void SetAnimalIndex(int index) {
        lstWatchListName.setSelectedIndex(index);
        lstWatchListThreat.setSelectedIndex(index);
    }
    
    private void ObserverIndex(int index) {
        int newIndex;
        
        newIndex = IndexFix(index);
        SetObserverIndex(newIndex);
    }
    
    private void SetObserverIndex(int index) {
        lstObserverName.setSelectedIndex(index);
        lstAddress.setSelectedIndex(index);
        lstPhoneNo.setSelectedIndex(index);
        lstEmail.setSelectedIndex(index);
    }
    
    
