    private void btnEditAnimalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditAnimalActionPerformed
        selectedIndex = lstWatchListName.getSelectedIndex()-1;
        String[] threatLevels = {"1", "2", "3", "4", "5" };
        JFrame frame = new JFrame("Threat Level");
        
        if (selectedIndex < 0) {
            Utilities.InfoBox("Please select an animal from the list.", "Remove Animal");
        }
        else {
            try {
            int input = Integer.parseInt((String) JOptionPane.showInputDialog(frame, "Please enter new threat level", "Threat Level",
                    JOptionPane.QUESTION_MESSAGE, null, threatLevels, threatLevels[0])); //combo box must have corect value           
                int index = selectedIndex;
                Animal x = BWA.watchList.get(index);
                x.setThreatLevel(input);
                Utilities.InfoBox("Threat level updated", "Threat level update");
                PopulateWatchList(); //refresh list
                SetAnimalIndex(selectedIndex +1); //set old index
            }
            catch (HeadlessException | NumberFormatException e) {} 
        }
    }//GEN-LAST:event_btnEditAnimalActionPerformed

