    private void btnContractActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnContractActionPerformed
        selectedIndex = lstObserverName.getSelectedIndex()-1;
        if (selectedIndex < 0) {
            Utilities.InfoBox("Please selected an observer from the list", "New call");
        }
        else if (BWA.observers.get(selectedIndex) instanceof ProfessionalOb) {
            Utilities.InfoBox("Observer must not already have a contract", "New Contract");
        }
        else {
            NewContract newContract = new NewContract();
            newContract.setVisible(true);
        }
    }//GEN-LAST:event_btnContractActionPerformed

